function dmxLoadContent($targetDiv, fromUrl, alwaysHide, callback){
    if ($targetDiv.is(':empty')){
        showLoading();
        $.ajax({method:'get', type:'html', url:fromUrl})
            .done(function(result){
                $targetDiv.html(result);
                if( typeof callback == "function" ){
                    callback();
                }
            })
            .fail(function() {
                hideLoading();
            }

            )
            .always(function(){
                if (alwaysHide||false)
                    hideLoading();
            }
        );
    }
}

